import React from 'react';
import { Link, useParams, useHistory } from 'react-router-dom';
import { deleteDeck, readDeck } from '../utils/api';
import Card from '../Card/Card';
//import ErrorMessage from '../Layout/ErrorMessage';

export const Deck = ({ decks }) => {
  const { deckId } = useParams();
  console.log('decks', decks);
  const [error, setError] = useState(undefined);
  const [deck, setDeck] = useState({});

  useEffect(() => {
    const abortController = new AbortController();

    async function loadDeck() {
      const response = await readDeck(deckId, abortController.signal);
      const deckFromAPI = await response.json();
      setDeck(deckFromAPI);
    }
    loadDeck();
    return () => abortController.abort();
  }, []);

  // useEffect(() => {
  //   const abortController = new AbortController();

  //   readDeck(deckId, abortController.signal).then(setDeck).catch(setError);

  //   return () => abortController.abort();
  // }, []);

  if (error) {
    return <ErrorMessage error={error} />;
  }

  //const deck = decks.find((deck) => deck.id === Number(deckId));
  const history = useHistory();
  console.log('deck', deck);
  const handleDelete = async (id) => {
    const result = window.confirm(
      'Delete this deck? \nYou will not be able to recover it.'
    );

    if (result) {
      await deleteDeck(id).then(() => history.push('/'));

      // ((response) => {
      // removeDeck(id);
      // });
    }
  };

  // function removeDeck(id) {
  //   setDecks(decks.filter((deck) => deck.id !== id));
  // }
  if (deck) {
    console.log('deck cards', deck.cards);
    const list = deck.cards.map((card) => <Card key={card.id} card={card} />);
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>

          <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">
            Edit
          </Link>
          <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">
            Study
          </Link>
          <Link
            to={`/decks/${deck.id}/cards/new`}
            className="btn btn-secondary"
          >
            Add Cards
          </Link>

          <button className="btn btn-danger" onClick={handleDelete}>
            Delete
          </button>
        </div>
        <div>
          <section className="row">{list}</section>
        </div>
      </div>
    );
  }
  return <div>No such deck</div>;
};

export default Deck;
