import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { deleteDeck, readDeck } from '../utils/api';
import Card from '../Card/Card';
import ErrorMessage from '../Layout/ErrorMessage';

export const Deck = () => {
  const { deckId } = useParams();
  const [deck, setDeck] = useState({});
  const [error, setError] = useState(undefined);

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(Number(deckId), abortController.signal)
      .then(setDeck)
      .catch(setError);
    return () => abortController.abort();
  }, []);

  if (error) {
    return <ErrorMessage error={error} />;
  }
  // const handleDelete = async (id) => {
  //   const result = window.confirm(
  //     'Delete this deck? \nYou will not be able to recover it.'
  //   );

  //   if (result) {
  //     deleteDeck(id).then((response) => {
  //       // removeDeck(id);
  //     });
  //   }
  // };

  // function removeDeck(id) {
  //   setDecks(decks.filter((deck) => deck.id !== id));
  // }
  if (deck) {
    console.log(deck.cards);
    const list = deck.cards.map((card) => <Card key={card.id} card={card} />);
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>

          <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">
            Edit
          </Link>
          <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">
            Study
          </Link>
          <Link
            to={`/decks/${deck.id}/cards/new`}
            className="btn btn-secondary"
          >
            Add Cards
          </Link>

          <button className="btn btn-danger">Delete</button>
        </div>
        <div>
          <section className="row">{list}</section>
        </div>
      </div>
    );
  }
};

export default Deck;
