import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { listCards, readDeck } from '../utils/api';
import ErrorMessage from '../Layout/ErrorMessage';
import classNames from '../utils/class-names';

export const Study = () => {
  const { deckId } = useParams();
  const [deck, setDeck] = useState({});
  const [cards, setCards] = useState([]);
  const [error, setError] = useState(undefined);
  const [flipped, setFlipped] = useState(false);

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setDeck).catch(setError);
    listCards(deckId, abortController.signal).then(setCards).catch(setError);
    return () => abortController.abort();
  }, []);
  function flipCard() {
    setFlipped((prevState) => !prevState);
  }
  if (error) {
    return <ErrorMessage error={error} />;
  }

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <a href="/">Home</a>
          </li>
          <li className="breadcrumb-item" aria-current="page">
            <a href={`/decks/${deck.id}`}>{deck.name}</a>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            Study
          </li>
        </ol>
      </nav>
      <h1>Study: {deck.name}</h1>
      <div className="col-sm-12">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Card 1 of {cards.length}</h5>

            <p className="card-text">{!flipped ? 'card front' : 'card back'}</p>
            <button className="btn btn-secondary" onClick={flipCard}>
              Flip
            </button>

            <button
              className={classNames({
                'btn btn-secondary': true,
                invisible: !flipped,
                visible: flipped,
              })}
              onClick={console.log('next card')}
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </>
  );
};
export default Study;
