import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { listDecks } from '../utils/api';
// import decks from '..../data/db.json';

export const List = () => {
  const [decks, setDecks] = useState([]);
  useEffect(() => {
    const listFromAPI = listDecks();
    setDecks(listFromAPI);
  }, [decks]);

  return (
    <div>
      <button type="button" class="btn btn-secondary far fa-plus-square">
        Create Deck
      </button>
      {decks.map((deck) => (
        <div class="card">
          <div class="card-header">Featured</div>
          <div class="card-body">
            <h5 class="card-title">{deck.name}</h5>
            <p class="card-text">{deck.description}</p>
            <a href="#" className="btn btn-secondary far fa-eye">
              View
            </a>
            <a href="#" className="btn btn-primary fas fa-chalkboard-teacher">
              Study
            </a>
          </div>
        </div>
      ))}
    </div>
  );
};
export default List;
