import React, { useState, useEffect } from 'react';
import Deck from '../Deck/Deck.js';
import { listDecks } from '../utils/api';

import ErrorMessage from './ErrorMessage';
import { deleteDeck } from '../utils/api';
import { Link } from 'react-router-dom';

export const List = () => {
  const [decks, setDecks] = useState([]);
  const [error, setError] = useState(undefined);

  const handleDelete = async () => {
    const result = window.confirm(
      'Delete this deck? \nYou will not be able to recover it.'
    );

    if (result) {
      deleteDeck(deck.id).then((response) => {
        removeDeck(deck.id);
      });
    }
  };

  function removeDeck(id) {
    setDecks(decks.filter((deck) => deck.id !== id));
  }
  useEffect(() => {
    const abortController = new AbortController();
    listDecks(abortController.signal).then(setDecks).catch(setError);
    return () => abortController.abort();
  }, []);

  if (error) {
    return <ErrorMessage error={error} />;
  }
  const list = decks.map((deck) => (
    // <Deck key={deck.id} deck={deck} removeDeck={removeDeck} />
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{deck.name}</h5>
        <p className="card-text">{deck.description}</p>

        <Link to={`/decks/${deck.id}`} className="btn btn-secondary">
          View
        </Link>
        <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">
          Edit
        </Link>
        <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">
          Study
        </Link>
        <Link to={`/decks/${deck.id}/cards/new`} className="btn btn-secondary">
          Add Cards
        </Link>
        <p>{`${deck.cards.length} cards`}</p>
        <button className="btn btn-danger" onClick={handleDelete}>
          Delete
        </button>
      </div>
      {/* <div>
        <section className="row">{list}</section>
      </div> */}
    </div>
  ));
  return (
    <main className="container">
      <Link to="decks/new" className="btn btn-secondary">
        Create Deck
      </Link>
      <section className="row">{list}</section>
    </main>
  );
};
export default List;
