import React from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import Card from '../Card/Card';
import { deleteDeck, readDeck } from '../utils/api';

export const Deck = ({ decks }) => {
  const { deckId } = useParams();
  const history = useHistory();
  const deck = decks.find((deck) => deck.id === Number(deckId));
  ///////////
  useEffect(() => {
    const abortController = new AbortController();

    async function handleDelete() {
      const result = window.confirm(
        'Delete this deck? \nYou will not be able to recover it.'
      );
      if (result) {
        try {
          deleteDeck({ deckId }).then(history.push('/'));
        } catch (error) {}
        if (error.name === 'AbortError') {
          console.log('Aborted');
        } else {
          throw error;
        }
      }
    }

    return () => {
      abortController.abort();
    };
  }, []);
  ///////////
  // const handleDelete = async () => {
  //   const result = window.confirm(
  //     'Delete this deck? \nYou will not be able to recover it.'
  //   );
  //   if (result) {
  //     deleteDeck({ deckId }).then(history.push('/'));
  //   }
  // };

  if (deck) {
    const list = deck.cards.map((card) => <Card key={card.id} card={card} />);
    return (
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <a href="/">Home</a>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              {deck.name}
            </li>
          </ol>
        </nav>
        <div>
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>

          <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">
            Edit
          </Link>
          <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">
            Study
          </Link>
          <Link
            to={`/decks/${deck.id}/cards/new`}
            className="btn btn-secondary"
          >
            Add Cards
          </Link>

          <button
            className="btn btn-danger"
            id={deck.id}
            onClick={handleDelete}
          >
            Delete
          </button>
        </div>
        <div>
          <h2>Cards</h2>
          <section>{list}</section>
        </div>
      </div>
    );
  }
  return <div>No such deck</div>;
};

export default Deck;
